import React from 'react';
import '/App.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
const photos = [
    {
        name: "Photo 1",
        url: "/images/shoes1.jpeg"
    },
    {
        name: "Photo 2",
        url: "/images/shoes2.jpg"
    },
    {
        name: "Photo 3",
        url: "/images/shoes3.jpg"
    },
    {
        name: "Photo 4",
        url: "/images/shoes4.jpg"
    }
]
class Carousel extends React.Component {
    render() {
        const settings = {
            autoplay: true,
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            arrows: true,
            slidesToScroll: 1,
            className: "slides"
           }
    return (
        <div className="App">
        <Slider {...settings}>
            {photos.map((photo) => {
                return(
                    <div>
                        <img width="100%" src={photo.url}/>
                    </div>
                )
            })}

        </Slider>
        </div>
          )
        }
}
export default Carousel;
