import React from 'react';
import ProductItems from '../ProductItems/ProductItems';
class Filter extends React.Component {
    constructor(props) {
        super(props);

        this.state= {
            price: "default"
        }
    }
    handlePriceDropDownSelect = (event) => {
        this.setState({
            price: event.target.value
        })
    }
    render() {
        return(
            <div>
            <h2 className="product-head">Take a look at some of our products! </h2>
                <section className="myBtnContainer">
                    <select class="productType" value={type} onChange={this.handlePriceDropDown}>
                    <option value="default">Show All</option>
                    <option value="btn1 btnShoes"> Shoes</option>
                    <option value="btn1 btnAccessories"> Accessories</option>
                </select>
                <select class="productPrice" value={price} onChange={this.handlePriceDropDown}>
                    <option value="btn1 btnPrices-100">$0 - $100</option>
                    <option value="btn1 btnPrices-200">$100 - $200</option>
                    <option value="btn1 btnPrices-300">$200 - $300</option>
                </select>
            </section>
                    <div className="container">
                        {data.map((ItemInfo, index)=> {
                            if (price === "defautl" ) {
                                return <ProductItems product={ItemInfo} />
                            } else if (price === ItemInfo.price )
                            return <ProductItems product={ItemInfo} />
                        })}
                    </div>
            </div>
        )
    }
}

export default Filter;
