import React from 'react';
import ProductItems from '../ProductItems/ProductItems';
import data from '../ProductsPage/data.json';

class Filter extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            price: "default"
        }
    }
    handlePriceDropDownSelect = (event) => {
        this.setState({
            price: event.target.value
        })
    }
    render() {
        const {price} = this.state;
        return(
            <div>
            <h2 className="product-head">Take a look at some of our products! </h2>
                <section className="myBtnContainer">
                    <select id="productType">
                    <option value="default">Show All</option>
                    <option value="Shoes">Shoes</option>
                    <option value="Accessories">Accessories</option>
                </select>
                <select id="productPrice" value={price} onChange={this.handlePriceDropDownSelect}>
                    <option value="default">Show All</option>
                    <option value="price0to100">$0 - $100</option>
                    <option value="price100to200">$100 - $200</option>
                    <option value="price200to300">$200 - $300</option>
                </select>
            </section>
                    <div className="productItemContainer">
                        {data.map((ItemInfo, index) => {
                            if (price === "default" ) {
                                return 
                                <div>
                                    <div className="gallery__item">
                        <article className={(ItemInfo.price)}>
                        <img className="gallery__img" src={ItemInfo.img} alt={ItemInfo.alt}/>
                        <h3>{ItemInfo.productName}</h3>
                        <p>{ItemInfo.productCost}</p>
                        <p>{ItemInfo.productDescription}</p>
                        <button className="btn1">Add To Cart</button>
                        </article>
                    </div>
                                 </div>
                        }
                        })}
                    </div>
            </div>
        )
    }
}


export default Filter;
